import React, {useState} from 'react';
import AppGrid from "./AppGrid";
import Image from "./Image";

function BoxMachine(props) {
    const [showModal, setShowModal] = useState(false)

    function Modal() {
        return (
            <>
                <div
                    className='fixed text-gray-500 flex items-center justify-center overflow-auto z-50 bg-black bg-opacity-40 left-0 right-0 top-0 bottom-0" x-transition:enter="transition ease duration-300" x-transition:enter-start="opacity-0" x-transition:enter-end="opacity-100" x-transition:leave="transition ease duration-300" x-transition:leave-start="opacity-100" x-transition:leave-end="opacity-0'>

                </div>
            </>
        )
    }

    return (
        <>
            <AppGrid grid={3}>
                <div className="h-full max-w-lg mx-auto bg-indigo-100 border-2 border-indigo-400 border-opacity-50 rounded-lg overflow-hidden shadow-md">
                    <Image/>

                    <div className="py-2 px-4">
                        <h1
                            className="text-xl font-medium leading-6 tracking-wide text-gray-800 hover:text-blue-500 cursor-pointer"
                        >
                            <a href="blog/detail">Name: Machine No.01</a>
                        </h1>
                    </div>
                    <div className="px-4 space-y-2 mb-4">
                        <p className="text-gray-600 font-normal leading-5 tracking-wide">
                            Address: 1823-12
                        </p>

                        <button
                            onClick={() => {setShowModal(true)}}
                            type="button"
                            className="border border-indigo-500 bg-indigo-500 text-white rounded-md px-4 py-2 transition ease-in duration-700 ease select-none hover:bg-indigo-700 focus:outline-none focus:shadow-outline"
                        >
                            See more...
                        </button>
                    </div>
                </div>
            </AppGrid>

            <Modal/>
        </>
    );
}

export default BoxMachine;
