import React from 'react';
import './AppModal.css'
import AppGrid from "./AppGrid";

function AppModal(props) {
    const {setShowModal} = props

    function onCloseModal(modal) {
        const modalToClose = document.querySelector('.main-modal');
        modalToClose.classList.remove('fadeIn');
        modalToClose.classList.add('fadeOut');
        setTimeout(() => {
            modalToClose.style.display = 'none';
        }, 500);
    }

    return (
        <div>
            {/*component*/}
            <div
                className="min-w-screen h-screen animated fadeIn faster fixed left-0 top-0 flex justify-center items-center inset-0 z-50 outline-none focus:outline-none bg-gray-600 bg-opacity-80"
                id="modal-id">
            </div>
        </div>
    );
}

export default AppModal;
