import React from 'react';
import './AppModal.css'
import AppGrid from "./AppGrid";

function AppModal(props) {
    const {setShowModal, product} = props

    function onCloseModal(modal) {
        const modalToClose = document.querySelector('.main-modal');
        modalToClose.classList.remove('fadeIn');
        modalToClose.classList.add('fadeOut');
        setTimeout(() => {
            modalToClose.style.display = 'none';
            setShowModal(false);
        }, 500);
    }

    if (product.length >= 0) {
        const productList = product.map((data, i) => {
            return (
                <div
                    className="main-modal min-w-screen h-screen animated fadeIn faster fixed left-0 top-0 flex justify-center items-center inset-0 z-50 outline-none focus:outline-none bg-gray-600 bg-opacity-80"
                    id="modal-id">
                    <div className="w-full h-full p-5 relative mx-auto my-auto rounded-xl shadow-lg  bg-white">
                        <div className="flex justify-between items-center pb-3">
                            <p className="text-2xl font-bold text-gray-500">Machine no.01</p>
                            <div
                                className="modal-close cursor-pointer z-50"
                                onClick={() => {
                                    onCloseModal()
                                }}
                            >
                                <svg className="fill-current text-gray-500" xmlns="http://www.w3.org/2000/svg"
                                     width="18"
                                     height="18"
                                     viewBox="0 0 18 18">
                                    <path
                                        d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z">
                                    </path>
                                </svg>
                            </div>
                        </div>
                        <div className="">
                            <AppGrid grid={3}>
                                <div className="text-center p-5 flex-auto justify-center">
                                    <img src='/img/bottle.png' className='bottle mx-auto'/>
                                    <h2 className="text-xl font-bold py-4 ">Pepsi</h2>
                                    <p className="text-sm text-gray-500 px-8">ราคา: ฿ 15 </p>
                                    <p className="text-sm text-gray-500 px-8">จำนวน: 20</p>

                                    <div className="p-3  mt-2 text-center space-x-4 md:block">
                                        <button
                                            className="mb-2 md:mb-0 bg-white px-5 py-2 text-sm shadow-sm font-medium tracking-wider border text-gray-600 rounded-full hover:shadow-lg hover:bg-gray-100">
                                            Buy
                                        </button>
                                    </div>
                                </div>
                            </AppGrid>
                        </div>
                    </div>
                </div>
            )
        })
    }

    return (
        <div>

        </div>
    );
}

export default AppModal;
