import React from 'react';

function AppModal(props) {
    const {setShowModal} = props

    const openModal = (modal) => {
        const modalToOpen = document.querySelector('.'+modal);
        modalToOpen.classList.remove('fadeOut');
        modalToOpen.classList.add('fadeIn');
        modalToOpen.style.display = 'flex';
    }

    return (
        <div>
            {/*component*/}
            <div
                className="min-w-screen h-screen animated fadeIn faster  fixed  left-0 top-0 flex justify-center items-center inset-0 z-50 outline-none focus:outline-none bg-gray-600 bg-opacity-80"
                id="modal-id">
                <div className="w-full h-full p-5 relative mx-auto my-auto rounded-xl shadow-lg  bg-white">
                    {/*content*/}
                    <div className="">
                        {/*body*/}
                        <div className="text-center p-5 flex-auto justify-center">
                            <h2 className="text-xl font-bold py-4 ">Are you sure?</h2>
                            <p className="text-sm text-gray-500 px-8">Do you really want to delete your account?
                                This process cannot be undone</p>
                        </div>
                        {/*footer*/}
                        <div className="p-3  mt-2 text-center space-x-4 md:block">
                            <button
                                onClick={() => {
                                    setShowModal(false)
                                }}
                                className="mb-2 md:mb-0 bg-white px-5 py-2 text-sm shadow-sm font-medium tracking-wider border text-gray-600 rounded-full hover:shadow-lg hover:bg-gray-100">
                                Cancel
                            </button>
                            <button
                                className="mb-2 md:mb-0 bg-red-500 border border-red-500 px-5 py-2 text-sm shadow-sm font-medium tracking-wider text-white rounded-full hover:shadow-lg hover:bg-red-600">Delete
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AppModal;
